package es.grupo04.backend;

import java.util.Arrays;
import java.util.List;

import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;

@Controller
public class GreetingController {

    @GetMapping("/")
    public String home(Model model) {
        // Datos simulados para la vista
        model.addAttribute("navbar_home", "ByteMarket");
        model.addAttribute("categories", Arrays.asList("Electr√≥nica", "Ropa", "Hogar"));
        model.addAttribute("featured_title", "Destacados");
        
        List<Product> featuredProducts = Arrays.asList(
            new Product("Laptop", "1200", "data:image/png;base64,", "/product/1"),
            new Product("Smartphone", "800", "data:image/png;base64,", "/product/2"),
            new Product("Smartphone", "800", "/images/phone.jpg", "/product/2"),
            new Product("Smartphone", "800", "/images/phone.jpg", "/product/2"),
            new Product("Smartphone", "800", "/images/phone.jpg", "/product/2"),
            new Product("Smartphone", "800", "/images/phone.jpg", "/product/2"),
            new Product("Smartphone", "800", "/images/phone.jpg", "/product/2"),
            new Product("Smartphone", "800", "/images/phone.jpg", "/product/2")
        );
        model.addAttribute("featured_products", featuredProducts);
        
        List<Product> otherProducts = Arrays.asList(
            new Product("Auriculares", "150", "data:image/png;base64,", "/product/3"),
            new Product("Reloj Inteligente", "200", "data:image/png;base64,", "/product/4"),
            new Product("Auriculares", "150", "/images/headphones.jpg", "/product/3"),
            new Product("Reloj Inteligente", "200", "/images/watch.jpg", "/product/4"),
            new Product("Auriculares", "150", "/images/headphones.jpg", "/product/3"),
            new Product("Reloj Inteligente", "200", "/images/watch.jpg", "/product/4")
        );
        model.addAttribute("other_products", otherProducts);
        model.addAttribute("other_products_title", "Historial de Ventas");
        return "home_template";
    }

    static class Product {
        public String name;
        public String price;
        public String image;
        public String url;

        public Product(String name, String price, String image, String url) {
            this.name = name;
            this.price = price;
            this.image = image;
            this.url = url;
        }
    }
}